// import logo from './logo.svg';
import React, { useEffect, useRef, useState } from 'react';
import './App.css';

function App() {
  const textRef = useRef(null);
  const [isOverflowing, setIsOverflowing] = useState(false);

  useEffect(() => {
    const paragraph = textRef.current;
    if (paragraph.scrollHeight > paragraph.clientHeight) {
      setIsOverflowing(true);
    }
  }, []);
  return (
   <main>
    <header>
      <a herf="#" className="logo">My Blog</a>
      <nav>
        <a herf="#">Login</a>
        <a herf="#">Register</a>

      </nav>
    </header>
    <hr></hr>
    <div className="posts">

      <div className="head">
        <h2>Elon Musk's xAI raises $6B from Valor, a16z, and Sequoia</h2>
        <h4>Ivan Mehta</h4>
      </div>
      <div className="desc">
      <p
       ref={textRef}
       className={`test2 ${isOverflowing ? 'ellipsis' : ''}`}
       >Elon Musk's AI startup, xAI, has raised $6 billion in a new funding round, it said today, in one of the largest deals in the red-hot nascent space, as he shores up capital to aggressively compete with rivals including OpenAI, Microsoft, and Google.</p>
      </div>
      <img src="https://techcrunch.com/wp-content/uploads/2024/04/GettyImages-2147789850.jpg?resize=1200,800"></img>
    </div>
   </main>
  );
}

export default App;
